import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import Carousel3D from '../components/Carousel3D';

const Home = () => {
  const [isContentVisible, setIsContentVisible] = useState(false);
  const [isMobileContentVisible, setIsMobileContentVisible] = useState(false);
  const contentRef = useRef(null);

  useEffect(() => {
    // Forzar scroll hacia arriba
    window.scrollTo(0, 0);

    // NO bloquear scroll vertical - permitir scroll normal en la página

    // Lazy loading con Intersection Observer
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsContentVisible(true);
            observer.unobserve(entry.target);
          }
        });
      },
      {
        threshold: 0.01, // Se activa cuando solo el 1% del elemento es visible
        rootMargin: '100px' // Se activa 100px antes de que sea visible
      }
    );

    if (contentRef.current) {
      observer.observe(contentRef.current);
    }

    // Fallback: si después de 2 segundos no se ha activado, mostrar el contenido
    const fallbackTimer = setTimeout(() => {
      if (!isContentVisible) {
        setIsContentVisible(true);
      }
    }, 2000);

    // En mobile: mostrar contenido al hacer scroll
    const handleScroll = () => {
      if (window.innerWidth < 768) {
        setIsMobileContentVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Limpiar al desmontar el componente
    return () => {
      if (contentRef.current) {
        observer.unobserve(contentRef.current);
      }
      clearTimeout(fallbackTimer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);



  return (
    <>
      <h1 className="sr-only">Featured Pieces</h1>

      {/* 3D-like PNG Carousel */}
      <div className="relative mt-28 md:mt-20 lg:mt-24 xl:mt-28 flex justify-center">
        <Carousel3D />
      </div>

      {/* Grid completo de 4 columnas - CON FADE-IN */}
      <div 
        ref={contentRef}
        className={`relative mt-16 sm:mt-20 md:mt-16 lg:mt-18 xl:mt-20 px-3 sm:px-4 md:px-8 lg:px-12 xl:px-16 2xl:px-20 max-w-none transition-all duration-1000 ease-out ${
          isContentVisible 
            ? 'opacity-100 translate-y-0' 
            : 'opacity-0 translate-y-8'
        }`}
      >
        <div className="flex flex-col gap-6 md:gap-8 lg:gap-10 w-full">

          {/* Título principal */}
          <div className="text-center lg:text-left w-full">
            <h2 className="font-bold text-2xl sm:text-3xl md:text-2xl lg:text-xl xl:text-2xl text-black mb-6 sm:mb-8 md:mb-8 lg:mb-5 leading-tight sm:leading-[0.9] md:leading-[0.95] lg:leading-[0.95]" style={{fontFamily: 'Moma Sans'}}>
              Bid to own the most iconic<br />
              protests on artwork
            </h2>

            {/* Flecha indicadora - SOLO EN MOBILE */}
            <div className={`md:hidden flex justify-center mt-6 sm:mt-8 transition-opacity duration-500 ${
              window.innerWidth < 1024 
                ? (isMobileContentVisible ? 'opacity-0' : 'opacity-100') 
                : 'opacity-100'
            }`}>
              <div className="animate-bounce">
                <svg 
                  className="w-6 h-6 text-gray-600" 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M19 14l-7 7m0 0l-7-7m7 7V3" 
                  />
                </svg>
              </div>
            </div>
          </div>

          {/* Contenedor para texto y botón que se mueve como bloque */}
          <div className={`transition-all duration-500 ${
            window.innerWidth < 768 
              ? (isMobileContentVisible ? '-mt-10' : 'mt-0') 
              : 'mt-0'
          }`}>

            {/* Primer párrafo */}
            <div className={`text-center lg:text-left w-full px-10 xl:px-0 mb-6 transition-opacity duration-500 ${
              window.innerWidth < 768 
                ? (isMobileContentVisible ? 'opacity-100' : 'opacity-10') 
                : 'opacity-100'
            }`}>
              <p className="text-sm sm:text-base md:text-sm lg:text-base text-black leading-relaxed sm:leading-tight md:leading-[1.1] lg:leading-[1.2] w-full" style={{fontFamily: 'Moma Sans'}}>
                Art is a form of human expression. Protest is too. So isn't protest against art the highest expression of art itself?
              </p>
            </div>

            {/* Segundo párrafo */}
            <div className={`text-center lg:text-left w-full px-10 xl:px-0 mb-6 transition-opacity duration-500 ${
              window.innerWidth < 768 
                ? (isMobileContentVisible ? 'opacity-100' : 'opacity-10') 
                : 'opacity-100'
            }`}>
              <p className="text-sm sm:text-base md:text-sm lg:text-base text-black leading-relaxed sm:leading-tight md:leading-[1.1] lg:leading-[1.2] w-full" style={{fontFamily: 'Moma Sans'}}>
                This sequence transforms some of the most iconic protests of our time into unique pieces, allowing any collector to own them not as headlines, but as real works of art.
              </p>
            </div>

            {/* Botón */}
            <div className={`text-center w-full transition-opacity duration-500 ${
              window.innerWidth < 768 
                ? (isMobileContentVisible ? 'opacity-100' : 'opacity-10') 
                : 'opacity-100'
            }`}>
              <Link to="/gallery" className="bg-transparent border-2 border-black text-black font-bold px-5 sm:px-6 md:px-5 lg:px-6 py-3 sm:py-3 md:py-3 lg:py-3 text-sm sm:text-base md:text-sm lg:text-sm uppercase tracking-wide hover:bg-black hover:text-white transition-colors duration-200">
                DISCOVER THE PIECES
              </Link>
            </div>

          </div>

        </div>


      </div>
    </>
  );
};

export default Home;
