import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Header = () => {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path;
  };

  const isStorePage = location.pathname === '/store';

  return (
    <header className={`fixed inset-x-0 top-0 z-[1000] h-14 flex items-center justify-between px-4 md:px-10 xl:px-20 ${
      isStorePage ? 'store-page' : 'bg-white text-black'
    }`} style={isStorePage ? {backgroundColor: '#000000'} : {}}>
      <Link to="/" className={`font-bold text-lg ${isStorePage ? '' : 'text-black'}`} style={isStorePage ? {color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'} : {}}>
        {isStorePage ? '[CAPITALISM]' : '[CAPITALISM]'}
      </Link>
      <nav className="flex items-center gap-5 whitespace-nowrap">
        <Link 
          to="/" 
          className={`font-bold no-underline hover:underline text-[0.95rem] ${
            isStorePage ? '' : 'text-black hover:text-gray-600'
          } ${isActive('/') ? 'underline' : ''}`}
          style={isStorePage ? {color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'} : {}}
        >
          {isStorePage ? 'ARTIVISM' : 'Artivism'}
        </Link>
        <Link 
          to="/gallery" 
          className={`font-bold no-underline hover:underline text-[0.95rem] ${
            isStorePage ? '' : 'text-black hover:text-gray-600'
          } ${isActive('/gallery') ? 'underline' : ''}`}
          style={isStorePage ? {color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'} : {}}
        >
          {isStorePage ? 'PIECES' : 'Pieces'}
        </Link>
        <button 
          className={`font-bold no-underline hover:underline text-[0.95rem] bg-transparent border-none cursor-pointer ${
            isStorePage ? '' : 'text-black hover:text-gray-600'
          }`}
          style={isStorePage ? {color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'} : {}}
        >
          {isStorePage ? 'EXHIBITION' : 'Exhibition'}
        </button>
        <Link 
          to="/store" 
          className={`font-bold no-underline hover:underline text-[0.95rem] ${
            isStorePage ? '' : 'text-black hover:text-gray-600'
          } ${isActive('/store') ? 'underline' : ''}`}
          style={isStorePage ? {color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'} : {}}
        >
          {isStorePage ? 'WEAPONS' : 'Weapons'}
        </Link>
      </nav>
    </header>
  );
};

export default Header;
