import React, { useState, useRef, useEffect } from 'react';

const Store = () => {
  const carouselRef = useRef(null);
  const [hoverPreview, setHoverPreview] = useState(null);
  const [previewPosition, setPreviewPosition] = useState({ x: 0, y: 0 });
  const [currentProductIndex, setCurrentProductIndex] = useState(0);

  // Datos de los productos de la tienda
  const baseSlides = [
    { id: 1, src: '/assets/img/pure-patata.png', title: 'Pure Patata', artist: 'Food Art', year: '2025', color: '#FF0000', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 2, src: '/assets/img/marker.png', title: 'Marker Stop Off', artist: 'Office Art', year: '2025', color: '#00FF00', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 3, src: '/assets/img/pea-soup.png', title: 'Pea Soup', artist: 'Food Art', year: '2025', color: '#0000FF', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 4, src: '/assets/img/marker.png', title: 'Tomato Soup', artist: 'Food Art', year: '2025', color: '#FFFF00', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 5, src: '/assets/img/pea-soup.png', title: 'Spray Can', artist: 'Street Art', year: '2025', color: '#FF00FF', preview: 'https://static.dw.com/image/68105658_605.jpg' }
  ];

  // Generar múltiples copias para scroll infinito
  const slides = [];
  const copies = 3;
  for (let c = 0; c < copies; c++) {
    baseSlides.forEach(slide => {
      slides.push({
        ...slide,
        id: `${slide.id}-copy-${c}`
      });
    });
  }

  // Función para posicionar el preview en hover
  const positionPreview = (e, slide) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Solo mostrar preview en el 60% central de la imagen
    const shouldShow = x > rect.width * 0.2 && x < rect.width * 0.8;

    if (shouldShow) {
      const pad = 4; // Offset del cursor
      setPreviewPosition({
        x: e.clientX + pad,
        y: e.clientY - 130 + pad // Centrar verticalmente
      });
      setHoverPreview({
        color: slide.color,
        preview: slide.preview
      });
    }
  };

  // Event handlers para el preview
  const handleMouseEnter = (e, slide) => {
    positionPreview(e, slide);
  };

  const handleMouseMove = (e, slide) => {
    positionPreview(e, slide);
  };

  const handleMouseLeave = () => {
    setHoverPreview(null);
  };

  // Auto-scroll infinito
  useEffect(() => {
    const BASE = baseSlides.length;
    const COPIES = 3;
    const MID = Math.floor(COPIES / 2);
    const slideWidth = 350;
    const gapPx = 8;
    const unitWidth = slideWidth + gapPx;

    let baseWidth = unitWidth * BASE;
    let offsetPx = -baseWidth * MID;
    let velocity = 0;

    const AUTO_VX = 1.0;

    let isScrolling = false;
    let isWheeling = false;
    let autoPaused = false;

    const applyTransform = () => {
      if (carouselRef.current) {
        carouselRef.current.style.transform = `translate3d(${Math.round(offsetPx)}px, 0, 0)`;
      }
    };

    const animate = () => {
      const autoV = autoPaused ? 0 : AUTO_VX;
      offsetPx += (velocity * 0.3) + autoV;

      velocity *= 0.94;

      offsetPx = ((offsetPx % baseWidth) + baseWidth) % baseWidth - baseWidth * MID;

      // Calcular qué producto está en el centro
      const centerPosition = Math.abs(offsetPx);
      const productIndex = Math.round(centerPosition / unitWidth) % BASE;
      setCurrentProductIndex(productIndex);

      applyTransform();
      requestAnimationFrame(animate);
    };

    animate();

    let wheelLock = false;
    const onWheel = (e) => {
      e.preventDefault();
      e.stopPropagation();
      isWheeling = true;

      setTimeout(() => {
        isWheeling = false;
      }, 40);

      if (wheelLock) return;
      const delta = Math.abs(e.deltaX) > Math.abs(e.deltaY) ? e.deltaX : e.deltaY;
      if (Math.abs(delta) < 20) return;
      wheelLock = true;

      if (delta > 0) {
        velocity -= unitWidth / 6;
      } else {
        velocity += unitWidth / 6;
      }
      velocity = Math.max(-unitWidth, Math.min(unitWidth, velocity));

      setTimeout(() => { wheelLock = false; }, 90);
    };

    const section = carouselRef.current?.closest('section');
    if (section) {
      section.addEventListener('wheel', onWheel, { passive: false });
      section.addEventListener('mouseenter', () => { autoPaused = true; });
      section.addEventListener('mouseleave', () => { autoPaused = false; });
    }

    return () => {
      if (section) {
        section.removeEventListener('wheel', onWheel);
        section.removeEventListener('mouseenter', () => { autoPaused = true; });
        section.removeEventListener('mouseleave', () => { autoPaused = false; });
      }
    };
  }, [baseSlides.length]);

  return (
    <main className="mt-14 pb-[calc(3.5rem+env(safe-area-inset-bottom))] bg-black min-h-screen">
      <h1 className="sr-only">Weapons Store</h1>

      {/* Header Section */}
      <section className="text-center mb-16 px-4 md:px-10 xl:px-20">
        <h2 className="text-[1.5rem] md:text-[2rem] lg:text-[2.5rem] font-bold text-[#FF0002] mb-4">
          Choose Your Weapon
        </h2>
      </section>

      {/* 3D Carousel Section */}
      <section className="relative w-screen left-1/2 -translate-x-1/2">
        <div className="relative w-full h-[50vh] md:h-[55vh] xl:h-[60vh] overflow-hidden">
          <div 
            ref={carouselRef}
            className="relative h-full flex items-center gap-2"
            style={{
              width: `${slides.length * 350}px`,
              minWidth: '100vw'
            }}
          >
            {slides.map((slide, index) => (
              <figure
                key={slide.id}
                className="flex-shrink-0 flex items-center justify-center transition-all duration-300 ease-in-out cursor-pointer"
                style={{
                  width: '350px',
                  height: '100%'
                }}
                onMouseEnter={(e) => handleMouseEnter(e, slide)}
                onMouseMove={(e) => handleMouseMove(e, slide)}
                onMouseLeave={handleMouseLeave}
                onClick={() => window.location.href = '/gioconda'}
              >
                <img
                  src={slide.src}
                  alt={slide.title}
                  className="block w-full h-auto max-w-full max-h-full object-contain select-none"
                  draggable={false}
                />
              </figure>
            ))}
          </div>
        </div>

        {/* Product Info */}
        <div className="mt-6 md:mt-8 text-center px-4 md:px-10 xl:px-20">
          <h3 className="text-[1rem] md:text-[1.25rem] lg:text-[1.5rem] font-bold text-white mb-3 transition-all duration-300">
            {baseSlides[currentProductIndex].title}
          </h3>
          <p className="text-[0.85rem] md:text-[0.9rem] lg:text-[1rem] text-gray-300 mb-6 transition-all duration-300">
            {baseSlides[currentProductIndex].artist} • {baseSlides[currentProductIndex].year}
          </p>
          <a
            href="/gioconda"
            className="inline-block bg-[#FF0002] text-white px-6 md:px-8 py-2 md:py-3 rounded-lg font-semibold hover:bg-[#dc2626] transition-all duration-300 text-[0.85rem] md:text-[0.9rem] lg:text-[1rem]"
          >
            View Details
          </a>
        </div>

        {/* Preview en hover */}
        {hoverPreview && (
          <div 
            className="fixed z-[2000] pointer-events-none overflow-hidden"
            style={{
              left: previewPosition.x + 'px',
              top: previewPosition.y + 'px',
              width: '260px',
              aspectRatio: '16 / 9',
              background: hoverPreview.preview ? 'white' : hoverPreview.color
            }}
          >
            {hoverPreview.preview && (
              <img 
                src={hoverPreview.preview}
                alt="Preview"
                className="w-full h-full object-contain"
              />
            )}
          </div>
        )}
      </section>
    </main>
  );
};

export default Store;
