import React, { useEffect } from 'react';
import Carousel3D from '../components/Carousel3D';

const Home = () => {
  useEffect(() => {
    // Bloquear scroll vertical en Home
    document.body.style.overflow = 'hidden';

    // Entry animation for elements
    const animTargets = [
      ...document.querySelectorAll('main > *'),
      ...document.querySelectorAll('main section > *'),
      ...document.querySelectorAll('main article')
    ];

    animTargets.forEach((el, i) => {
      el.classList.add('reveal');
      const delay = Math.min(i * 50, 600);
      setTimeout(() => el.classList.add('in'), delay);
    });

    // Limpiar al desmontar el componente
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  return (
    <>
      <h1 className="sr-only">Featured Pieces</h1>

      {/* 3D-like PNG Carousel */}
      <div className="relative mt-32 flex justify-center">
        <Carousel3D />
      </div>

      {/* Tres bloques de texto explicativos */}
      <div className="mt-16 px-1 md:px-4 xl:px-8 max-w-none mx-auto">
        <div className="grid grid-cols-1 md:grid-cols-12 gap-4 md:gap-6 w-full">

          {/* Bloque izquierdo - Título y Call to Action */}
          <div className="text-center md:text-left md:col-span-5 w-full">
            <h2 className="font-bold text-sm md:text-base text-black mb-2">[ARTIVISM]</h2>
            <p className="text-xs md:text-sm text-black font-medium leading-[1.2] md:leading-[1.0] w-full">
              BID TO OWN THE MOST ICONIC<br />
              PROTESTS ON ARTWORK
            </p>
          </div>

          {/* Bloque central - Declaración filosófica */}
          <div className="text-center md:text-left md:col-span-5 w-full">
            <p className="text-xs md:text-sm text-black leading-[1.2] md:leading-[1.0] w-full">
              Art is a form of human expression.<br />
              Protest is too.<br />
              So isn't protest against art the highest<br />
              expression of art itself?
            </p>
          </div>

          {/* Bloque derecho - Explicación del concepto */}
          <div className="text-center md:text-left md:col-span-2 w-full">
            <p className="text-xs md:text-sm text-black leading-[1.2] md:leading-[1.0] w-full">
              This sequence transforms some of the<br />
              most iconic protests of our time into<br />
              unique pieces, allowing any collector to<br />
              own them not as headlines, but as real<br />
              works of art.
            </p>
          </div>

        </div>
      </div>
    </>
  );
};

export default Home;
