import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

const Gallery = () => {
  const galleryItems = [
    {
      id: 1,
      title: "Tomato Soup over \"La Gioconda\"",
      artist: "Capitalism",
      year: "2025",
      image: "https://upload.wikimedia.org/wikipedia/commons/6/6a/Mona_Lisa.jpg",
      link: "/gioconda"
    },
    {
      id: 2,
      title: "Mashed potatoes on Monet's Les Meules",
      artist: "Capitalism",
      year: "2022",
      image: "https://upload.wikimedia.org/wikipedia/commons/4/4e/Claude_Monet_-_Meules_%28W_1273%29.jpg",
      link: "/lesmeules"
    },
    {
      id: 3,
      title: "Fossil fuels on Klimt's Death and Life",
      artist: "Capitalism",
      year: "2022",
      image: "https://arthive.com/res/media/img/oy800/work/2ba/536354@2x.jpg",
      link: "/deathandlife"
    },
    {
      id: 4,
      title: "Intervention on \"Girl with a Pearl Earring\"",
      artist: "Capitalism",
      year: "2025",
      image: "https://upload.wikimedia.org/wikipedia/commons/a/af/Vincent_van_Gogh_-_The_Sower_-_c._17-28_June_1888.jpg",
      link: "/thesower"
    },
    {
      id: 5,
      title: "Intervention on \"Self‑portrait\"",
      artist: "Capitalism",
      year: "2025",
      image: "https://upload.wikimedia.org/wikipedia/commons/2/2e/Rembrandt_-_De_Nachtwacht_-_Google_Cultural_Institute.jpg",
      link: "/gioconda"
    },
    {
      id: 6,
      title: "Intervention on \"Birth of Venus\"",
      artist: "Capitalism",
      year: "2025",
      image: "https://upload.wikimedia.org/wikipedia/commons/1/1c/Birth_of_Venus_Botticelli.jpg",
      link: "/gioconda"
    },
    {
      id: 7,
      title: "Intervention on \"The Kiss\"",
      artist: "Capitalism",
      year: "2025",
      image: "https://upload.wikimedia.org/wikipedia/commons/7/71/The_Kiss_-_Gustav_Klimt_-_Google_Cultural_Institute.jpg",
      link: "/gioconda"
    },
    {
      id: 8,
      title: "Intervention on \"Olympia\"",
      artist: "Capitalism",
      year: "2025",
      image: "https://upload.wikimedia.org/wikipedia/commons/0/0d/Olympia_-_Edouard_Manet_-_Google_Cultural_Institute.jpg",
      link: "/gioconda"
    }
  ];

  useEffect(() => {
    // Entry animation for gallery items
    const animTargets = document.querySelectorAll('article');

    animTargets.forEach((el, i) => {
      el.classList.add('opacity-0', 'translate-y-4');
      const delay = Math.min(i * 100, 800); // Máximo 800ms de delay
      setTimeout(() => {
        el.classList.add('transition-all', 'duration-700', 'ease-out');
        el.classList.remove('opacity-0', 'translate-y-4');
      }, delay);
    });
  }, []);

  return (
    <main className="mt-14 pb-[calc(3.5rem+env(safe-area-inset-bottom))] px-4 md:px-10 xl:px-20 max-w-full">
      <h1 className="sr-only">Pieces</h1>

      {/* Gallery grid */}
      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-x-12 gap-y-14">
        {galleryItems.map((item) => (
          <article key={item.id} className="flex flex-col">
            <Link to={item.link} className="block bg-[#f2f2f2] p-4">
              <div className="w-full aspect-[3/4] flex items-center justify-center overflow-hidden">
                <img 
                  src={item.image} 
                  alt={item.title}
                  className="w-full h-full object-contain max-w-[80%] max-h-[80%]"
                />
              </div>
            </Link>
            <div className="mt-4 leading-snug">
              <p className="m-0">{item.title}</p>
              <p className="m-0 font-bold">{item.artist}</p>
              <p className="m-0">{item.year}</p>
            </div>
          </article>
        ))}
      </section>
    </main>
  );
};

export default Gallery;
