import React, { useState, useRef, useEffect } from 'react';

const Store = () => {
  const carouselRef = useRef(null);
  const [currentProductIndex, setCurrentProductIndex] = useState(0);

  // Datos de los productos de la tienda
  const baseSlides = [
    { id: 1, src: '/assets/img/pure-patata.png', title: 'Pure Patata', artist: 'Food Art', year: '2025', color: '#FF0000', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 2, src: '/assets/img/marker.png', title: 'Marker Stop Off', artist: 'Office Art', year: '2025', color: '#00FF00', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 3, src: '/assets/img/pea-soup.png', title: 'Pea Soup', artist: 'Food Art', year: '2025', color: '#0000FF', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 4, src: '/assets/img/marker.png', title: 'Tomato Soup', artist: 'Food Art', year: '2025', color: '#FFFF00', preview: 'https://static.dw.com/image/68105658_605.jpg' },
    { id: 5, src: '/assets/img/pea-soup.png', title: 'Spray Can', artist: 'Street Art', year: '2025', color: '#FF00FF', preview: 'https://static.dw.com/image/68105658_605.jpg' }
  ];

  // Generar múltiples copias para scroll infinito
  const slides = [];
  const copies = 3;
  for (let c = 0; c < copies; c++) {
    baseSlides.forEach(slide => {
      slides.push({
        ...slide,
        id: `${slide.id}-copy-${c}`
      });
    });
  }

  // Auto-scroll infinito
  useEffect(() => {
    // Bloquear scroll vertical en Store
    document.body.style.overflow = 'hidden';

    const BASE = baseSlides.length;
    const COPIES = 3;
    const MID = Math.floor(COPIES / 2);
    const slideWidth = 350;
    const gapPx = 8;
    const unitWidth = slideWidth + gapPx;

    let baseWidth = unitWidth * BASE;
    let offsetPx = -baseWidth * MID;
    let velocity = 0;

    const AUTO_VX = 1.0;

    let isScrolling = false;
    let isWheeling = false;
    let autoPaused = false;

    const applyTransform = () => {
      if (carouselRef.current) {
        carouselRef.current.style.transform = `translate3d(${Math.round(offsetPx)}px, 0, 0)`;
      }
    };

    const animate = () => {
      const autoV = autoPaused ? 0 : AUTO_VX;
      offsetPx += (velocity * 0.3) + autoV;

      velocity *= 0.94;

      offsetPx = ((offsetPx % baseWidth) + baseWidth) % baseWidth - baseWidth * MID;

      // Calcular qué producto está en el centro
      const centerPosition = Math.abs(offsetPx);
      const productIndex = Math.round(centerPosition / unitWidth) % BASE;
      setCurrentProductIndex(productIndex);

      applyTransform();
      requestAnimationFrame(animate);
    };

    animate();

    let wheelLock = false;
    const onWheel = (e) => {
      e.preventDefault();
      e.stopPropagation();
      isWheeling = true;

      setTimeout(() => {
        isWheeling = false;
      }, 40);

      if (wheelLock) return;
      const delta = Math.abs(e.deltaX) > Math.abs(e.deltaY) ? e.deltaX : e.deltaY;
      if (Math.abs(delta) < 20) return;
      wheelLock = true;

      if (delta > 0) {
        velocity -= unitWidth / 6;
      } else {
        velocity += unitWidth / 6;
      }
      velocity = Math.max(-unitWidth, Math.min(unitWidth, velocity));

      setTimeout(() => { wheelLock = false; }, 90);
    };

    const section = carouselRef.current?.closest('section');
    if (section) {
      section.addEventListener('wheel', onWheel, { passive: false });
      section.addEventListener('mouseenter', () => { autoPaused = true; });
      section.addEventListener('mouseleave', () => { autoPaused = false; });
    }

    // Cleanup: restaurar scroll al desmontar el componente
    return () => {
      document.body.style.overflow = 'auto';
      if (section) {
        section.removeEventListener('wheel', onWheel);
        section.removeEventListener('mouseenter', () => { autoPaused = true; });
        section.removeEventListener('mouseleave', () => { autoPaused = false; });
      }
    };
  }, [baseSlides.length]);

  return (
    <main className="mt-14 pb-[calc(3.5rem+env(safe-area-inset-bottom))] bg-white min-h-screen flex items-start pt-20">
      <h1 className="sr-only">Weapons Store</h1>

      {/* 3D Carousel Section */}
      <section className="relative w-screen left-1/2 -translate-x-1/2">
        <div className="relative w-full h-[50vh] md:h-[55vh] xl:h-[60vh] overflow-hidden">
          <div 
            ref={carouselRef}
            className="relative h-full flex items-center gap-2"
            style={{
              width: `${slides.length * 350}px`,
              minWidth: '100vw'
            }}
          >
            {slides.map((slide, index) => (
              <figure
                key={slide.id}
                className="flex-shrink-0 flex flex-col items-center justify-center transition-all duration-300 ease-in-out cursor-pointer"
                style={{
                  width: '350px',
                  height: '100%'
                }}
                onClick={() => window.location.href = '/gioconda'}
              >
                <div className="bg-[#f2f2f2] p-4 w-full h-full flex flex-col items-center justify-center">
                  <img
                    src={slide.src}
                    alt={slide.title}
                    className="block w-full h-auto max-w-full max-h-[60%] object-contain select-none mb-4"
                    draggable={false}
                  />
                  <div className="text-center w-full">
                    <h3 className="text-[0.9rem] font-bold text-gray-800 mb-2 transition-all duration-300" style={{ fontFamily: 'Moma Sans, sans-serif' }}>
                      {slide.title}
                    </h3>
                    <p className="text-[0.75rem] text-gray-600 mb-3 transition-all duration-300" style={{ fontFamily: 'Moma Sans, sans-serif' }}>
                      {slide.artist} • {slide.year}
                    </p>
                    <button className="inline-block bg-black text-white px-4 py-2 font-semibold transition-all duration-300 text-[0.75rem]" style={{ fontFamily: 'Moma Sans, sans-serif' }}>
                      BUY
                    </button>
                  </div>
                </div>
              </figure>
            ))}
          </div>
        </div>
      </section>
    </main>
  );
};

export default Store;
