import React, { useEffect } from 'react';
import Carousel3D from '../components/Carousel3D';

const Home = () => {
  useEffect(() => {
    // Bloquear scroll vertical en Home
    document.body.style.overflow = 'hidden';

    // Entry animation for elements
    const animTargets = [
      ...document.querySelectorAll('main > *'),
      ...document.querySelectorAll('main section > *'),
      ...document.querySelectorAll('main article')
    ];

    animTargets.forEach((el, i) => {
      el.classList.add('reveal');
      const delay = Math.min(i * 50, 600);
      setTimeout(() => el.classList.add('in'), delay);
    });

    // Limpiar al desmontar el componente
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  return (
    <>
      <h1 className="sr-only">Featured Pieces</h1>

      {/* 3D-like PNG Carousel */}
      <div className="relative mt-32 flex justify-center">
        <Carousel3D />

        {/* Caja de texto superpuesta en la parte inferior derecha */}
        <div className="absolute -bottom-8 md:-bottom-32 left-1/2 transform -translate-x-1/2 z-20 bg-black text-white p-6 max-w-md">
          <h2 className="font-bold text-lg text-white mb-3">[ARTIVISM]</h2>
          <p className="text-white mb-3"><u>BID TO OWN THE MOST ICONIC PROTESTS ON ARTWORK</u></p>
          <p className="leading-tight text-white text-sm mb-2">
            Art is a form of human expression. Protest is too.<br />
            Therefore, isn't protest against art the highest expression of art itself?
          </p>
          <p className="leading-tight text-white text-sm">
            This sequence transforms some of the most iconic protests of our time into unique pieces, 
            allowing any collector to own them not as headlines, but as what they truly are: works of art.
          </p>
        </div>
      </div>
    </>
  );
};

export default Home;
