import React, { useState, useEffect, useCallback } from 'react';

// ✅ Optimizado: Array movido fuera del componente para evitar recreación
const ART_PIECES = [
  {
    id: 1,
    title: "Pure Patata",
    artist: "Food Art",
    year: "2025",
    image: "/assets/img/pure-patata.png",
    link: "/gioconda"
  },
  {
    id: 2,
    title: "Marker Stop Off",
    artist: "Office Art",
    year: "2025",
    image: "/assets/img/marker.png",
    link: "/gioconda"
  },
  {
    id: 3,
    title: "Pea Soup",
    artist: "Food Art",
    year: "2025",
    image: "/assets/img/pea-soup.png",
    link: "/gioconda"
  },
  {
    id: 4,
    title: "Tomato Soup",
    artist: "Food Art",
    year: "2025",
    image: "/assets/img/marker.png",
    link: "/gioconda"
  },
  {
    id: 5,
    title: "Spray Can",
    artist: "Street Art",
    year: "2025",
    image: "/assets/img/pea-soup.png",
    link: "/gioconda"
  }
];

const Store = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isGlowing, setIsGlowing] = useState(false);

  // Block vertical scroll when component mounts
  useEffect(() => {
    document.body.classList.add('store-page-active');

    // Cleanup: remove class when component unmounts
    return () => {
      document.body.classList.remove('store-page-active');
    };
  }, []);

  // Optimizado: useCallback para evitar recrear funciones en cada render
  const nextProduct = useCallback(() => {
    setIsGlowing(true);
    setCurrentIndex(prev => (prev + 1) % ART_PIECES.length);
    setTimeout(() => setIsGlowing(false), 300);
  }, []);

  const prevProduct = useCallback(() => {
    setIsGlowing(true);
    setCurrentIndex(prev => (prev - 1 + ART_PIECES.length) % ART_PIECES.length);
    setTimeout(() => setIsGlowing(false), 300);
  }, []);



  return (
    <main className="min-h-screen flex flex-col px-4 md:px-10 xl:px-20 max-w-full text-red-500 store-page store-page-flicker" style={{backgroundColor: '#000000'}}>
      <h1 className="sr-only">Pieces</h1>

            {/* Centered Product Selector - Always in center */}
      <div className="absolute inset-0 flex flex-col items-center justify-center -mt-16">
        {/* Title positioned above the selector */}
        <div className="text-center mb-16">
          <h2 className="text-6xl font-bold uppercase tracking-wider animate-flicker glow-red" style={{color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'}}>
            Choose Your Weapon<span className="animate-blink">_</span>
          </h2>
        </div>

        {/* Simple Product Selector */}
        <div className="relative w-full max-w-8xl">


                {/* Product Display - Simple Carousel */}
        <div className="relative w-full">
          {/* Flecha izquierda - Posicionada absolutamente */}
          <button 
            onClick={prevProduct}
            className="absolute left-2 md:left-4 lg:left-6 top-1/2 -translate-y-1/2 text-2xl md:text-4xl lg:text-5xl select-none transition-all duration-300 z-10"
            style={{
              color: '#FF0002',
              textShadow: '0 0 8px rgba(255, 0, 2, 0.6)',
              filter: 'drop-shadow(0 0 4px rgba(255, 0, 2, 0.4))'
            }}
          >
            &#8592;
          </button>

          {/* Carrusel centrado */}
          <div className="flex justify-center">
            {/* Mobile: Solo imagen central */}
            <div className="block md:hidden">
              <img 
                src={ART_PIECES[currentIndex].image}
                className="w-48 h-48 opacity-100 border-2 rounded custom-pulse transition-all duration-300"
                style={{
                  borderColor: '#FF0002', 
                  boxShadow: isGlowing 
                    ? '0 0 40px rgba(255, 0, 2, 0.9), inset 0 0 40px rgba(255, 0, 2, 0.6)' 
                    : '0 0 20px rgba(255, 0, 2, 0.6)'
                }}
                draggable={false}
              />
            </div>

            {/* Desktop: 5 imágenes */}
            <div className="hidden md:flex gap-4 lg:gap-6 items-center">
              <img 
                src={ART_PIECES[(currentIndex - 2 + ART_PIECES.length) % ART_PIECES.length].image}
                className={`w-32 lg:w-56 h-32 lg:h-56 opacity-30 border-2 border-transparent rounded cursor-pointer transition-all duration-300`}
                onClick={() => setCurrentIndex((currentIndex - 2 + ART_PIECES.length) % ART_PIECES.length)}
                draggable={false}
              />
              <img 
                src={ART_PIECES[(currentIndex - 1 + ART_PIECES.length) % ART_PIECES.length].image}
                className={`w-32 lg:w-64 h-32 lg:h-64 opacity-50 border-2 border-transparent rounded cursor-pointer transition-all duration-300`}
                onClick={() => setCurrentIndex((currentIndex - 1 + ART_PIECES.length) % ART_PIECES.length)}
                draggable={false}
              />
              <img 
                src={ART_PIECES[currentIndex].image}
                className="w-48 lg:w-80 h-48 lg:h-80 opacity-100 border-2 rounded custom-pulse transition-all duration-300"
                style={{
                  borderColor: '#FF0002', 
                  boxShadow: isGlowing 
                    ? '0 0 40px rgba(255, 0, 2, 0.9), inset 0 0 40px rgba(255, 0, 2, 0.6)' 
                    : '0 0 20px rgba(255, 0, 2, 0.6)'
                }}
                draggable={false}
              />
              <img 
                src={ART_PIECES[(currentIndex + 1) % ART_PIECES.length].image}
                className={`w-40 lg:w-64 h-32 lg:h-64 opacity-50 border-2 border-transparent rounded cursor-pointer transition-all duration-300`}
                onClick={() => setCurrentIndex((currentIndex + 1) % ART_PIECES.length)}
                draggable={false}
              />
              <img 
                src={ART_PIECES[(currentIndex + 2) % ART_PIECES.length].image}
                className={`w-32 lg:w-56 h-32 lg:h-56 opacity-30 border-2 border-transparent rounded cursor-pointer transition-all duration-300`}
                onClick={() => setCurrentIndex((currentIndex + 2) % ART_PIECES.length)}
                draggable={false}
              />
            </div>
          </div>

          {/* Flecha derecha - Posicionada absolutamente */}
          <button 
            onClick={nextProduct}
            className="absolute right-2 md:right-4 lg:right-6 top-1/2 -translate-y-1/2 text-2xl md:text-4xl lg:text-5xl select-none transition-all duration-300 z-10"
            style={{
              color: '#FF0002',
              textShadow: '0 0 8px rgba(255, 0, 2, 0.6)',
              filter: 'drop-shadow(0 0 4px rgba(255, 0, 2, 0.4))'
            }}
          >
            &#8594;
          </button>
        </div>

        {/* Descripción - Siempre debajo de la imagen central */}
        <div className="mt-8 text-center">
          <p className="text-lg font-bold uppercase mb-2" style={{color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'}}>
            {ART_PIECES[currentIndex].title}
          </p>
          <p className="text-sm uppercase mb-4" style={{color: '#FF0002', fontFamily: 'CAPITALISM_SPEND, sans-serif'}}>
            {ART_PIECES[currentIndex].year}
          </p>
          <button 
            className="border-2 px-8 py-2 font-bold uppercase tracking-wider transition-all duration-300" 
            style={{
              borderColor: '#FF0002', 
              color: '#FF0002',
              fontFamily: 'CAPITALISM_SPEND, sans-serif'
            }}
            onMouseEnter={(e) => {
              e.target.style.backgroundColor = '#FF0002';
              e.target.style.color = '#000000';
            }}
            onMouseLeave={(e) => {
              e.target.style.backgroundColor = 'transparent';
              e.target.style.color = '#FF0002';
            }}
          >
            BUY
          </button>
        </div>
      </div>


      </div>
    </main>
  );
};

export default Store;
