import React, { useState, useEffect, useCallback } from 'react';

// ✅ Optimizado: Array movido fuera del componente para evitar recreación
const ART_PIECES = [
  {
    id: 1,
    title: "Pure Patata",
    artist: "Food Art",
    year: "2025",
    image: "/assets/img/pure-patata.png",
    link: "/gioconda"
  },
  {
    id: 2,
    title: "Marker Stop Off",
    artist: "Office Art",
    year: "2025",
    image: "/assets/img/marker.png",
    link: "/gioconda"
  },
  {
    id: 3,
    title: "Pea Soup",
    artist: "Food Art",
    year: "2025",
    image: "/assets/img/pea-soup.png",
    link: "/gioconda"
  },
  {
    id: 4,
    title: "Tomato Soup",
    artist: "Food Art",
    year: "2025",
    image: "/assets/img/marker.png",
    link: "/gioconda"
  },
  {
    id: 5,
    title: "Spray Can",
    artist: "Street Art",
    year: "2025",
    image: "/assets/img/pea-soup.png",
    link: "/gioconda"
  }
];

const Store = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isGlowing, setIsGlowing] = useState(false);

  // Block vertical scroll when component mounts
  useEffect(() => {
    document.body.classList.add('store-page-active');

    // Cleanup: remove class when component unmounts
    return () => {
      document.body.classList.remove('store-page-active');
    };
  }, []);

  // Optimizado: useCallback para evitar recrear funciones en cada render
  const nextProduct = useCallback(() => {
    setIsGlowing(true);
    setCurrentIndex(prev => (prev + 1) % ART_PIECES.length);
    setTimeout(() => setIsGlowing(false), 300);
  }, []);

  const prevProduct = useCallback(() => {
    setIsGlowing(true);
    setCurrentIndex(prev => (prev - 1 + ART_PIECES.length) % ART_PIECES.length);
    setTimeout(() => setIsGlowing(false), 300);
  }, []);



  return (
    <main className="mt-14 pb-[calc(3.5rem+env(safe-area-inset-bottom))] px-4 md:px-10 xl:px-20 max-w-full bg-black min-h-screen">
      <h1 className="sr-only">Weapons Store</h1>

      {/* Header Section */}
      <section className="text-center mb-16">
        <h2 className="text-4xl md:text-5xl lg:text-6xl font-bold text-[#FF0002] mb-4">
          Choose Your Weapon
        </h2>
      </section>

      {/* Carousel Section */}
      <section className="relative w-full max-w-6xl mx-auto">
        {/* Carousel Display */}
        <div className="p-8 rounded-lg">
          <div className="relative flex items-center justify-center">
            {/* Previous Arrow */}
            <button
              onClick={prevProduct}
              className="absolute left-4 z-10 bg-white/80 hover:bg-white text-gray-800 rounded-full p-2 shadow-lg transition-all duration-300 hover:scale-110"
              style={{ transform: 'translateX(-100%)' }}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>

            {/* Product Images */}
            <div className="flex items-center justify-center gap-4 md:gap-8">
              {/* Previous Product */}
              <div className="hidden md:block">
                <img
                  src={ART_PIECES[(currentIndex - 1 + ART_PIECES.length) % ART_PIECES.length].image}
                  alt={ART_PIECES[(currentIndex - 1 + ART_PIECES.length) % ART_PIECES.length].title}
                  className="w-32 h-32 md:w-40 md:h-40 object-cover rounded-lg opacity-60"
                />
              </div>

              {/* Current Product */}
              <div className="relative">
                <img
                  src={ART_PIECES[currentIndex].image}
                  alt={ART_PIECES[currentIndex].title}
                  className="w-48 h-48 md:w-64 md:h-64 lg:w-80 lg:h-80 object-cover rounded-lg shadow-2xl"
                />
              </div>

              {/* Next Product */}
              <div className="hidden md:block">
                <img
                  src={ART_PIECES[(currentIndex + 1) % ART_PIECES.length].image}
                  alt={ART_PIECES[(currentIndex + 1) % ART_PIECES.length].title}
                  className="w-32 h-32 md:w-40 md:h-40 object-cover rounded-lg opacity-60"
                />
              </div>
            </div>

            {/* Next Arrow */}
            <button
              onClick={nextProduct}
              className="absolute right-4 z-10 bg-white/80 hover:bg-white text-gray-800 rounded-full p-2 shadow-lg transition-all duration-300 hover:scale-110"
              style={{ transform: 'translateX(100%)' }}
            >
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </div>
        </div>

        {/* Product Info */}
        <div className="mt-8 text-center">
          <h3 className="text-2xl md:text-3xl font-bold text-white mb-2">
            {ART_PIECES[currentIndex].title}
          </h3>
          <p className="text-lg text-gray-300 mb-6">
            {ART_PIECES[currentIndex].artist} • {ART_PIECES[currentIndex].year}
          </p>
          <a
            href={ART_PIECES[currentIndex].link}
            className="inline-block bg-[#FF0002] text-white px-8 py-3 rounded-lg font-semibold hover:bg-[#dc2626] transition-colors duration-300"
          >
            View Details
          </a>
        </div>
      </section>
    </main>
  );
};

export default Store;
