import React, { useEffect } from 'react';
import Carousel3D from '../components/Carousel3D';

const Home = () => {
  useEffect(() => {
    // Forzar scroll hacia arriba
    window.scrollTo(0, 0);

    // Bloquear scroll vertical en Home
    document.body.style.overflow = 'hidden';

    // Bloquear scroll vertical en mobile también
    const preventScroll = (e) => {
      // Bloquear scroll vertical completamente
      e.preventDefault();
    };

    // Solo aplicar a eventos de scroll, no a toques en el carrusel
    document.addEventListener('touchmove', preventScroll, { passive: false });
    // No bloquear touchstart para permitir interacciones normales

    // Entry animation for elements
    const animTargets = [
      ...document.querySelectorAll('main > *'),
      ...document.querySelectorAll('main section > *'),
      ...document.querySelectorAll('main article')
    ];

    animTargets.forEach((el, i) => {
      el.classList.add('reveal');
      const delay = Math.min(i * 50, 600);
      setTimeout(() => el.classList.add('in'), delay);
    });

    // Limpiar al desmontar el componente
    return () => {
      document.body.style.overflow = 'auto';
      document.removeEventListener('touchmove', preventScroll);
    };
  }, []);

  return (
    <>
      <h1 className="sr-only">Featured Pieces</h1>

      {/* 3D-like PNG Carousel */}
      <div className="relative mt-20 md:mt-32 flex justify-center">
        <Carousel3D />
      </div>

      {/* Tres bloques de texto explicativos */}
      <div className="mt-16 px-4 md:px-10 xl:px-20 max-w-none">
        <div className="grid grid-cols-1 md:grid-cols-12 gap-8 md:gap-12 w-full max-w-6xl">

          {/* Bloque izquierdo - Título principal */}
          <div className="text-center md:text-left md:col-span-5 w-full">
            <h2 className="font-bold text-2xl md:text-3xl text-black mb-6 leading-[0.9] md:leading-[0.95]" style={{fontFamily: 'Moma Sans'}}>
              Bid to own the most iconic<br />
              protests on artwork
            </h2>
          </div>

          {/* Bloque central - Declaración filosófica */}
          <div className="text-center md:text-left md:col-span-3 md:col-start-7 w-full">
            <p className="text-sm md:text-base text-black leading-relaxed md:leading-[1.2] w-full" style={{fontFamily: 'Moma Sans'}}>
              Art is a form of human expression. Protest is too. So isn't protest against art the highest expression of art itself?
            </p>
          </div>

          {/* Bloque derecho - Explicación del concepto */}
          <div className="text-center md:text-left md:col-span-4 md:col-start-10 w-full">
            <p className="text-sm md:text-base text-black leading-relaxed md:leading-[1.2] w-full" style={{fontFamily: 'Moma Sans'}}>
              This sequence transforms some of the most iconic protests of our time into unique pieces, allowing any collector to own them not as headlines, but as real works of art.
            </p>
          </div>

        </div>

        {/* Botón Call to Action */}
        <div className="flex justify-end mt-8 md:mt-10 pr-0 border-2 border-green-500 p-4 w-full">
          <button className="bg-transparent border-2 border-black text-black font-bold px-8 py-4 text-base uppercase tracking-wide hover:bg-black hover:text-white transition-colors duration-200 min-w-[200px]">
            DISCOVER THE PIECES
          </button>
        </div>
      </div>
    </>
  );
};

export default Home;
