import { useEffect, useRef, useCallback } from 'react';

export const useCarousel3D = (baseSlides, copies = 3) => {
  const carouselRef = useRef(null);
  const animationRef = useRef(null);
  const isAnimatingRef = useRef(false);
  const isPausedRef = useRef(false);
  const isVisibleRef = useRef(true);

  // Constantes del sistema
  const BASE = baseSlides.length;
  const MID = Math.floor(copies / 2);
  const slideWidth = 350;
  const gapPx = 8;
  const unitWidth = slideWidth + gapPx;
  const baseWidth = unitWidth * BASE;
  const AUTO_VX = 1.0;

  // Estado del carrusel
  const offsetRef = useRef(-baseWidth * MID);
  const velocityRef = useRef(0);

  // Función para aplicar transformación
  const applyTransform = useCallback(() => {
    if (carouselRef.current) {
      carouselRef.current.style.transform = `translate3d(${Math.round(offsetRef.current)}px, 0, 0)`;
    }
  }, []);

  // Función de animación optimizada
  const animate = useCallback(() => {
    // Solo animar si es necesario
    if (!isAnimatingRef.current || !isVisibleRef.current) {
      return;
    }

    // Lógica de animación
    const autoV = isPausedRef.current ? 0 : AUTO_VX;
    offsetRef.current += (velocityRef.current * 0.3) + autoV;

    // Decay de velocidad
    velocityRef.current *= 0.94;

    // Normalización de posición
    offsetRef.current = ((offsetRef.current % baseWidth) + baseWidth) % baseWidth - baseWidth * MID;

    // Aplicar transformación
    applyTransform();

    // Continuar solo si es necesario
    if (isAnimatingRef.current && isVisibleRef.current) {
      animationRef.current = requestAnimationFrame(animate);
    }
  }, [baseWidth, MID, AUTO_VX, applyTransform]);

  // Iniciar animación
  const startAnimation = useCallback(() => {
    if (!isAnimatingRef.current) {
      isAnimatingRef.current = true;
      animationRef.current = requestAnimationFrame(animate);
    }
  }, [animate]);

  // Parar animación
  const stopAnimation = useCallback(() => {
    isAnimatingRef.current = false;
    if (animationRef.current) {
      cancelAnimationFrame(animationRef.current);
      animationRef.current = null;
    }
  }, []);

  // Pausar animación
  const pauseAnimation = useCallback(() => {
    isPausedRef.current = true;
  }, []);

  // Reanudar animación
  const resumeAnimation = useCallback(() => {
    isPausedRef.current = false;
  }, []);

  // Control de visibilidad de la página
  useEffect(() => {
    const handleVisibilityChange = () => {
      isVisibleRef.current = !document.hidden;

      if (document.hidden) {
        // Página no visible → parar animación
        stopAnimation();
      } else {
        // Página visible → reanudar si es necesario
        if (isAnimatingRef.current) {
          startAnimation();
        }
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);

    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [startAnimation, stopAnimation]);

  // Control de intersección (cuando el carrusel está visible en pantalla)
  useEffect(() => {
    if (!carouselRef.current) return;

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            // Carrusel visible → reanudar animación
            isVisibleRef.current = true;
            if (isAnimatingRef.current) {
              startAnimation();
            }
          } else {
            // Carrusel no visible → parar animación
            isVisibleRef.current = false;
            stopAnimation();
          }
        });
      },
      { threshold: 0.1 } // 10% visible
    );

    observer.observe(carouselRef.current);

    return () => {
      observer.disconnect();
    };
  }, [startAnimation, stopAnimation]);

  // Iniciar animación al montar
  useEffect(() => {
    startAnimation();

    return () => {
      stopAnimation();
    };
  }, [startAnimation, stopAnimation]);

  // Control de scroll manual
  const handleWheel = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();

    const delta = Math.abs(e.deltaX) > Math.abs(e.deltaY) ? e.deltaX : e.deltaY;
    if (Math.abs(delta) < 20) return;

    if (delta > 0) {
      velocityRef.current -= unitWidth / 6;
    } else {
      velocityRef.current += unitWidth / 6;
    }

    velocityRef.current = Math.max(-unitWidth, Math.min(unitWidth, velocityRef.current));
  }, [unitWidth]);

  // Control de touch para mobile
  const handleTouchStart = useCallback((e) => {
    if (window.innerWidth < 768) {
      pauseAnimation();
    }
  }, [pauseAnimation]);

  const handleTouchEnd = useCallback((e) => {
    if (window.innerWidth < 768) {
      // No reanudar automáticamente en mobile
    }
  }, []);

  // Event listeners
  useEffect(() => {
    const section = carouselRef.current?.closest('section');
    if (!section) return;

    section.addEventListener('wheel', handleWheel, { passive: false });
    section.addEventListener('touchstart', handleTouchStart, { passive: true });
    section.addEventListener('touchend', handleTouchEnd, { passive: true });
    section.addEventListener('mouseenter', pauseAnimation);
    section.addEventListener('mouseleave', resumeAnimation);

    return () => {
      section.removeEventListener('wheel', handleWheel);
      section.removeEventListener('touchstart', handleTouchStart);
      section.removeEventListener('touchend', handleTouchEnd);
      section.removeEventListener('mouseenter', pauseAnimation);
      section.removeEventListener('mouseleave', resumeAnimation);
    };
  }, [handleWheel, handleTouchStart, handleTouchEnd, pauseAnimation, resumeAnimation]);

  return {
    carouselRef,
    startAnimation,
    stopAnimation,
    pauseAnimation,
    resumeAnimation
  };
};
