import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';

const Exhibition = () => {
  const [isContentVisible, setIsContentVisible] = useState(false);
  const [isMobileContentVisible, setIsMobileContentVisible] = useState(false);
  const contentRef = useRef(null);

  useEffect(() => {
    // Forzar scroll hacia arriba
    window.scrollTo(0, 0);

    // Lazy loading con Intersection Observer
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsContentVisible(true);
            observer.unobserve(entry.target);
          }
        });
      },
      {
        threshold: 0.01,
        rootMargin: '100px'
      }
    );

    if (contentRef.current) {
      observer.observe(contentRef.current);
    }

    // Fallback: si despuÃ©s de 2 segundos no se ha activado, mostrar el contenido
    const fallbackTimer = setTimeout(() => {
      if (!isContentVisible) {
        setIsContentVisible(true);
      }
    }, 2000);

    // En mobile: mostrar contenido al hacer scroll
    const handleScroll = () => {
      if (window.innerWidth < 768) {
        setIsMobileContentVisible(true);
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Limpiar al desmontar el componente
    return () => {
      if (contentRef.current) {
        observer.unobserve(contentRef.current);
      }
      clearTimeout(fallbackTimer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <>
      <h1 className="sr-only">Exhibition - Artivism</h1>

      {/* Header grÃ¡fico abstracto - inspirado en la imagen del MoMA */}
      <div className="relative mt-28 md:mt-20 lg:mt-24 xl:mt-28 w-full">
        <div className="w-full">
          <div className="relative h-64 md:h-80 lg:h-96 bg-gradient-to-br from-blue-500 via-pink-500 to-green-400 overflow-hidden">
            {/* Formas geomÃ©tricas abstractas */}
            <div className="absolute inset-0">
              {/* Forma azul base */}
              <div className="absolute bottom-0 left-0 w-2/3 h-3/4 bg-blue-600 transform rotate-12 origin-bottom-left"></div>
              {/* Forma rosa */}
              <div className="absolute bottom-0 right-0 w-1/2 h-2/3 bg-pink-500 transform -rotate-6 origin-bottom-right"></div>
              {/* Forma verde */}
              <div className="absolute top-1/4 right-1/4 w-1/3 h-1/2 bg-green-400 transform rotate-45 origin-center"></div>
            </div>

            {/* Overlay de texto */}
            <div className="absolute inset-0 flex items-center justify-center">
              <div className="text-center text-white">
                <h2 className="text-4xl md:text-6xl lg:text-7xl font-bold mb-4" style={{fontFamily: 'Moma Sans'}}>
                  EXHIBITION
                </h2>
                <p className="text-xl md:text-2xl font-medium opacity-90">
                  Artivism Digital Wall
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Contenido principal con grid de 2 columnas */}
      <div 
        ref={contentRef}
        className={`relative mt-16 sm:mt-20 md:mt-8 lg:mt-18 xl:mt-12 2xl:mt-20 px-3 sm:px-4 md:px-8 lg:px-12 xl:px-16 2xl:px-20 max-w-none transition-all duration-1000 ease-out ${
          isContentVisible 
            ? 'opacity-100 translate-y-0' 
            : 'opacity-0 translate-y-8'
        }`}
      >
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-16 xl:gap-20 w-full items-start">

          {/* Columna izquierda - Detalles de la exhibiciÃ³n */}
          <div className="text-center lg:text-left w-full">
            <div className="space-y-8">
              {/* TÃ­tulo de la exhibiciÃ³n */}
              <div>
                <h3 className="font-bold text-2xl sm:text-3xl md:text-2xl lg:text-xl xl:text-lg 2xl:text-xl text-black mb-4" style={{fontFamily: 'Moma Sans'}}>
                  Exhibition
                </h3>
                <p className="text-lg md:text-xl text-black mb-2">
                  <span className="underline">Artivism Digital Gallery</span>
                </p>
                <p className="text-base md:text-lg text-gray-700">
                  The Virtual Protest Art Collection
                </p>
              </div>

              {/* InformaciÃ³n de la exhibiciÃ³n */}
              <div>
                <p className="text-base md:text-lg text-black mb-4">
                  This exhibition is part of <strong>Artivism Digital Wall</strong>, featuring the most iconic protests transformed into unique digital artworks.
                </p>
              </div>

              {/* Call to action */}
              <div className="bg-green-50 p-6 rounded-lg">
                <h4 className="font-bold text-lg text-green-800 mb-3" style={{fontFamily: 'Moma Sans'}}>
                  Discover more as a member
                </h4>
                <Link 
                  to="/gallery" 
                  className="inline-flex items-center text-green-700 hover:text-green-800 font-medium transition-colors duration-200"
                >
                  Join today â
                </Link>
              </div>
            </div>
          </div>

          {/* Columna derecha - DescripciÃ³n del artista/proyecto */}
          <div className="text-center lg:text-left w-full">
            <div className="space-y-6">
              <blockquote className="text-lg md:text-xl text-black italic leading-relaxed">
                "I always wanted to make work that could be seen by anyone, anywhere, anytime," says the Artivism collective, innovators in the realm of digital protest art.
              </blockquote>

              <div className="space-y-4 text-base md:text-lg text-black leading-relaxed">
                <p>
                  Our work explores the <strong>aesthetic and conceptual possibilities of digital art</strong>, treating code as if it were paint, and protest as the highest form of artistic expression.
                </p>

                <p>
                  These pieces are <strong>accessible online by everyone</strong>, starting as digital sketches and then translated into interactive experiences that become autonomous artworks powered by algorithms and real-time data.
                </p>

                <p>
                  The exhibition presents a selection of our most iconic pieces, each representing a moment of protest that has been transformed into a lasting digital artwork, allowing viewers to experience the intersection of activism and art in a completely new way.
                </p>

                <p>
                  Our goal is for viewers to <strong>experience a state of immersion so complete that it becomes one with our digital world</strong>, blurring the lines between protest, art, and technology.
                </p>
              </div>

              <div className="text-sm text-gray-600 pt-4">
                <p>Curated by the Artivism Collective</p>
                <p>Department of Digital Activism & Art</p>
              </div>
            </div>
          </div>

        </div>

        {/* BotÃ³n de acciÃ³n principal */}
        <div className="text-center mt-12 md:mt-16">
          <Link 
            to="/gallery" 
            className="bg-transparent border-2 border-black text-black font-bold px-8 py-3 text-sm uppercase tracking-wide hover:bg-black hover:text-white transition-colors duration-200 inline-block"
          >
            EXPLORE THE EXHIBITION
          </Link>
        </div>
      </div>
    </>
  );
};

export default Exhibition;
