import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import Carousel3D from '../components/Carousel3D';

const Home = () => {
  useEffect(() => {
    // Forzar scroll hacia arriba
    window.scrollTo(0, 0);

    // NO bloquear scroll vertical - permitir scroll normal en la página

    // Entry animation for elements
    const animTargets = [
      ...document.querySelectorAll('main > *'),
      ...document.querySelectorAll('main section > *'),
      ...document.querySelectorAll('main article')
    ];

    animTargets.forEach((el, i) => {
      el.classList.add('reveal');
      const delay = Math.min(i * 50, 600);
      setTimeout(() => el.classList.add('in'), delay);
    });

    // Limpiar al desmontar el componente
    return () => {
      // No es necesario restaurar nada ya que no bloqueamos el scroll
    };
  }, []);

  return (
    <>
      <h1 className="sr-only">Featured Pieces</h1>

      {/* 3D-like PNG Carousel */}
      <div className="relative mt-16 md:mt-20 lg:mt-24 xl:mt-28 flex justify-center">
        <Carousel3D />
      </div>

      {/* Tres bloques de texto explicativos */}
      <div className="mt-8 sm:mt-10 md:mt-16 lg:mt-18 xl:mt-20 px-3 sm:px-4 md:px-8 lg:px-12 xl:px-16 2xl:px-20 max-w-none">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 sm:gap-5 md:gap-8 lg:gap-10 xl:gap-12 w-full">

          {/* Columna 1 - Título principal */}
          <div className="text-center md:text-left w-full md:col-span-2 lg:col-span-1">
            <h2 className="font-bold text-lg sm:text-xl md:text-lg lg:text-xl xl:text-2xl text-black mb-2 sm:mb-3 md:mb-4 lg:mb-5 leading-tight sm:leading-[0.9] md:leading-[0.95] lg:leading-[0.95]" style={{fontFamily: 'Moma Sans'}}>
              Bid to own the most iconic<br />
              protests on artwork
            </h2>
          </div>

          {/* Columna 2 - Primer párrafo del texto */}
          <div className="text-center md:text-left w-full md:col-span-2 lg:col-span-1">
            <p className="text-sm sm:text-base md:text-sm lg:text-base text-black leading-relaxed sm:leading-tight md:leading-[1.1] lg:leading-[1.2] w-full" style={{fontFamily: 'Moma Sans'}}>
              Art is a form of human expression. Protest is too. So isn't protest against art the highest expression of art itself?
            </p>
          </div>

          {/* Columna 3 - Segundo párrafo del texto */}
          <div className="text-center md:text-left w-full md:col-span-2 lg:col-span-1">
            <p className="text-sm sm:text-base md:text-sm lg:text-base text-black leading-relaxed sm:leading-tight md:leading-[1.1] lg:leading-[1.2] w-full" style={{fontFamily: 'Moma Sans'}}>
              This sequence transforms some of the most iconic protests of our time into unique pieces, allowing any collector to own them not as headlines, but as real works of art.
            </p>
          </div>

          {/* Columna 4 - Botón */}
          <div className="text-center md:text-center lg:text-right w-full md:col-span-2 lg:col-span-1 flex items-start justify-center lg:justify-end">
            <Link to="/gallery" className="bg-transparent border-2 border-black text-black font-bold px-5 sm:px-6 md:px-5 lg:px-6 py-3 sm:py-3 md:py-3 lg:py-3 text-sm sm:text-base md:text-sm lg:text-sm uppercase tracking-wide hover:bg-black hover:text-white transition-colors duration-200">
              DISCOVER THE PIECES
            </Link>
          </div>

        </div>
      </div>
    </>
  );
};

export default Home;
