import React from 'react';

const WeaponsPreloader = ({ isVisible }) => {
  // Verificaci칩n adicional para asegurar que se oculte
  if (!isVisible) return null;

  // Verificar que no estemos ya en la p치gina Store
  if (window.location.pathname === '/store') return null;

  return (
    <div 
      className="fixed inset-0 z-[99999] bg-black flex items-center justify-center store-page-flicker" 
      style={{ 
        top: 0, 
        left: 0, 
        right: 0, 
        bottom: 0,
        position: 'fixed',
        width: '100vw',
        height: '100vh'
      }}
    >
      {/* Scanlines overlay */}
      <div 
        className="absolute inset-0 pointer-events-none"
        style={{
          background: `repeating-linear-gradient(
            0deg,
            transparent,
            transparent 2px,
            rgba(255, 0, 2, 0.15) 2px,
            rgba(255, 0, 2, 0.15) 4px
          )`
        }}
      />

      <div className="text-center relative z-20">
        <div className="text-lg md:text-xl font-bold uppercase tracking-wider" style={{ fontFamily: 'CAPITALISM_SPEND, sans-serif', color: '#FF0002' }}>
          <span className="inline-block opacity-0 animate-fade-in" style={{ animationDelay: '0s', animationFillMode: 'forwards' }}>LOADING</span>
          <span className="inline-block opacity-0 animate-fade-in" style={{ animationDelay: '0.3s', animationFillMode: 'forwards' }}>WEAPON</span>
          <span className="inline-block opacity-0 animate-fade-in" style={{ animationDelay: '0.6s', animationFillMode: 'forwards' }}>STORE</span>
          <span className="inline-block">
            <span className="opacity-0 animate-fade-in" style={{ animationDelay: '0.9s', animationFillMode: 'forwards' }}>.</span>
            <span className="opacity-0 animate-fade-in" style={{ animationDelay: '1.1s', animationFillMode: 'forwards' }}>.</span>
            <span className="opacity-0 animate-fade-in" style={{ animationDelay: '1.3s', animationFillMode: 'forwards' }}>.</span>
          </span>
        </div>
      </div>
    </div>
  );
};

export default WeaponsPreloader;
